import React, { Component } from 'react';
import classes from './Dashboard.module.css';
import LineGraph from './Cart';

import {
	managerData,
	nationalAverageData,
	yearLabels,
	managerQuarterData,
	nationalAverageQuarterData,
	quarterLabels,
} from '../../mockData';

export default class Dashboard extends Component {
	state = {
		data: managerData,
		average: nationalAverageData,
		labels: yearLabels,
	};

	handleButtonClick = (e) => {
		const { value } = e.target;
		const isAnnual = value === 'annual';

		const newData = isAnnual ? managerData : managerQuarterData;
		const newLabels = isAnnual ? yearLabels : quarterLabels;
		const newAverage = isAnnual
			? nationalAverageData
			: nationalAverageQuarterData;

		this.setState({
			data: newData,
			average: newAverage,
			labels: newLabels,
		});
	};

	render() {
		const { data, average, labels } = this.state;
		return (
			<div className={classes.container}>
				<header>
					<img src={chartIcon} alt="bar chart icon" />
					<h1>Sales Dashboard</h1>
				</header>

				<div className={classes.buttonContainer}>
					<button value="annual" onClick={this.handleButtonClick}>
						Annual
					</button>

					<button value="lastquarter" onClick={this.handleButtonClick}>
						Last Quarter
					</button>
				</div>

				<LineGraph data={data} average={average} labels={labels} />
			</div>
		);
	}
}
