import React, { Component } from 'react';
import Chart from 'chart.js';
import classes from './LineGraph.module.css';
let myLineChart;

//--Chart Style Options--//
Chart.defaults.global.defaultFontFamily = "'PT Sans', sans-serif";
Chart.defaults.global.legend.display = false;
//--Chart Style Options--//

export default class LineGraph extends Component {
	chartRef = React.createRef();

	componentDidMount() {
		this.buildChart();
	}

	componentDidUpdate() {
		this.buildChart();
	}

	buildChart = () => {
		const myChartRef = this.chartRef.current.getContext('2d');
		const { data, average, labels } = this.props;

		myLineChart = new Chart(myChartRef, {
			type: 'line',
			data: {
				//Bring in data
				labels: labels,
				datasets: [
					{
						label: 'Sales',
						data: data,
						fill: false,
						borderColor: function (context) {
							const { height: chartHeight } = myChartRef.canvas;

							//---Gradient Styling---//
							let gradientFill = myChartRef.createLinearGradient(
								0,
								chartHeight / 2,
								0,
								chartHeight
							);
							gradientFill.addColorStop(0.1, 'rgb(152, 185, 171, 0.5)');
							gradientFill.addColorStop(0.5, 'rgb(152, 185, 171, 0.25)');
							gradientFill.addColorStop(0.9, 'rgb(152, 185, 171, 0.1)');
							//---Gradient Styling---//
							if (typeof myLineChart !== 'undefined') myLineChart.destroy();
						},
					},
				],
			},
			options: {
				//Customize chart options
			},
		});
	};

	render() {
		return (
			<div className={classes.graphContainer}>
				<canvas id="myChart" ref={this.chartRef} />
			</div>
		);
	}
}
