import React, { Component } from 'react';
import dynamic from 'next/dynamic';
const Chart = dynamic(() => import('react-apexcharts'), { ssr: false });
export default class MyApex extends Component {
	constructor(props) {
		super(props);

		this.state = {
			chart: {
				id: 'basic-bar',
				type: 'area',
				toolbar: {
					show: false,
				},
			},
			options: {
				grid: {
					strokeDashArray: 10,
				},
				dataLabels: {
					enabled: false,
				},
				responsive: [
					{
						breakpoint: 1200,
						options: {
							chart: {
								width: '100%',
							},
						},
					},
				],
				responsive: [
					{
						breakpoint: 1600,
						options: {
							chart: {
								width: '110%',
							},
						},
					},
				],
				xaxis: {
					categories: ['1 Dec', '8 Dec', '16 Dec', '31 Dec'],
				},
				stroke: {
					curve: 'smooth',
				},
			},
			series: [
				{
					name: 'series-1',
					data: [50, 150, 60, 150],
				},
			],
		};
	}
	render() {
		return (
			<div className="app">
				<div className="row">
					<div className="mixed-chart">
						<Chart
							options={this.state.options}
							series={this.state.series}
							type="area"
							width="600"
						/>
					</div>
				</div>
			</div>
		);
	}
}
