import React, { Component } from 'react';
import dynamic from 'next/dynamic';
const Chart = dynamic(() => import('react-apexcharts'), { ssr: false });
export default class MyApex extends Component {
	constructor(props) {
		super(props);

		this.state = {
			options: {
				grid: {
					strokeDashArray: 10,
				},
				chart: {
					id: 'basic-bar',
					type: 'area',
					toolbar: {
						show: false,
					},
				},
				dataLabels: {
					enabled: false,
				},
				xaxis: {
					categories: ['1 Dec', '8 Dec', '16 Dec', '31 Dec'],
				},
				stroke: {
					curve: 'smooth',
				},
			},
			series: [
				{
					name: 'series-1',
					data: [50, 150, 60, 150],
				},
			],
		};
	}
	render() {
		return (
			<div className="app">
				<div className="row">
					<div className="mixed-chart">
						<Chart
							options={this.state.options}
							series={this.state.series}
							type="area"
							width="700"
						/>
					</div>
				</div>
			</div>
		);
	}
}
