import React, { Component } from 'react';
import dynamic from 'next/dynamic';
const Chart = dynamic(() => import('react-apexcharts'), { ssr: false });

class DonutChart extends Component {
	constructor(props) {
		super(props);

		this.state = {
			series: [8, 1, 1],

			options: {
				labels: ['asd', 'Completed', 'C'],

				dataLabels: {
					enabled: false,
				},
				dropShadow: {
					enabled: false,
				},
				plotOptions: {
					pie: {
						expandOnClick: false,

						donut: {
							size: '90%',
							background: 'red',
							labels: {
								show: true,
								name: {
									show: true,
									formatter: function (val) {
										return val;
									},
								},
								value: {
									show: true,
									formatter: function (val) {
										return val;
									},
								},
							},
						},
					},
				},
			},
		};
	}

	render() {
		return (
			<div className="donut mt-2">
				<Chart
					options={this.state.options}
					series={this.state.series}
					type="donut"
					width="100%"
				/>
			</div>
		);
	}
}

export default DonutChart;
