import React, { Component } from 'react';
import { Media } from 'react-breakpoints';
import WindowSizeListener from 'react-window-size-listener';

import {
	AreaChart,
	Area,
	XAxis,
	YAxis,
	CartesianGrid,
	Tooltip,
} from 'recharts';
const data = [
	{ name: '1 Dec', uv: 4000, pv: 2400, amt: 2400 },
	{ name: '', uv: 3000, pv: 1398, amt: 2210 },
	{ name: '8 Dec', uv: 2000, pv: 9800, amt: 2290 },
	{ name: '', uv: 2780, pv: 3908, amt: 2000 },
	{ name: '16 Dec', uv: 1890, pv: 4800, amt: 2181 },
	{ name: '', uv: 2390, pv: 3800, amt: 2500 },
	{ name: '31 Dec', uv: 3490, pv: 4300, amt: 2100 },
];
export default class Chart extends Component {
	constructor(props) {
		super(props);
		this.state = { width: 0, chart: 0 };
	}
	render() {
		const { width } = this.state;
		return (
			<>
				<WindowSizeListener
					onResize={(windowSize) => {
						this.setState({ width: windowSize.windowHeight });
					}}
				/>
				<AreaChart
					{...(this.width
						? this.width < 1024
							? (width = 300)
							: (width = 500)
						: (width = 1000))}
					height={300}
					data={data}
					margin={{ top: 10, right: 50, left: 0, bottom: 0 }}
				>
					<defs>
						<linearGradient id="colorUv" x1="0" y1="0" x2="0" y2="1">
							<stop offset="5%" stopColor="#109CF1" stopOpacity={0.3} />
							<stop offset="95%" stopColor="#fff" stopOpacity={0} />
						</linearGradient>
					</defs>
					<XAxis
						dataKey="name"
						axisLine={false}
						padding={{ left: 32, right: 1 }}
						tickMargin={8}
						tickSize={0}
					/>
					<YAxis axisLine={false} tickSize={0} />

					<CartesianGrid vertical={false} strokeDasharray="1" strokeWidth={1} />
					<Tooltip />
					<Area
						type="monotone"
						dataKey="uv"
						dot={{ stroke: '#109CF1', strokeWidth: 2 }}
						stroke="#109CF1"
						strokeWidth={3}
						fillOpacity={1}
						fill="url(#colorUv)"
					/>
				</AreaChart>
			</>
		);
	}
}
